#!/usr/bin/python3
#-*- coding: utf-8 -*-

import sys
import time

import pyp11

print('Импорт сертификата')
#Для Linux
lib = '/usr/local/lib64/libls11sw2016.so'
#Облачный токен
#lib = '/usr/local/lib64/libls11cloud.so'
#Аппаратный токен
#lib = '/usr/local/lib64/librtpkcs11ecp_2.0.so'
#Для Windows
#lib='C:\Temp\ls11sw2016.dll'

#Загружаем библиотеку
aa = pyp11.loadmodule(lib)
print (aa)
try:
    bb = pyp11.login (aa, 0, '01234567')
except:
    print('Except login: ')
    e = sys.exc_info()[1]
    e1 = e.args[0]
    print (e1)

cert_der_hex = ''


print ('ImportCert')
lobj = pyp11.listobjects (aa, 0, 'cert')
if (len(lobj) == 0):
    print ('На токене нет объектов  типа сертификатов (CKO_CERTIFICATE)')
lcerts = pyp11.listcerts (aa, 0)
print (lcerts)
if (len(lcerts) == 0):
    print ('На токене нет сертификатов')
pyp11.logout(aa, 0)

ckaid = pyp11.importcert (aa, 0, cert_der_hex, "TEST_IMPORT")
print ('ImportCert END')
print (ckaid)
print ('LISTCERTSDER START')
lcerts = pyp11.listcerts (aa, 0)
print (lcerts)
if (len(lcerts) == 0):
    print ('На токене нет сертификатов')
    quit()
#Перебираем список сертификатов
for cert in lcerts:
    for v in cert:
        print (v + ':' + cert[v])
print ('LISTCERTSDER END')
lobj = pyp11.listobjects (aa, 0, 'cert', 'value')
#Перебираем список объектов
for obj in lobj:
#Информация об объекте
    for key in obj:
        print (key + ':' + obj[key])
print ('LISTOBJECTS END')
print ('Удаляем установленный сертификат. Подождите пять секунд')
dd = dict(pkcs11_id=ckaid)
print ('Если у вас извлекаемый токен, то можете переустановить его, \nчтобы проверить pyp11.closesession')
time.sleep(5)

try:
    lobj = pyp11.delete (aa, 0, 'cert', dd)
except:
    e = sys.exc_info()[1]
    e1 = e.args[0]
    print (e1)
#Закрываем сессию и повторяем команду
    pyp11.closesession(aa)
    lobj = pyp11.delete (aa, 0, 'cert', dd)

#Перебираем список объектов
lobj = pyp11.listobjects (aa, 0, "cert", 'value')
#lobj = pyp11.listobjects (aa, 0, "cert")
#pyp11.logout(aa, 0)
if (len(lobj) == 0):
    print ('На токене нет объектов  типа сертификатов (CKO_CERTIFICATE)')
    quit()
for obj in lobj:
#Информация об объекте
    for key in obj:
        print (key + ':' + obj[key])
print ('LISTOBJECTS END')


